export const dateFormat = {
  methods: {
    getDateAndTime: function(date) {
      if(date !== null){
        let hour = date.getHours();
        let minutes = date.getMinutes();
        let fullDate = `${date.getFullYear()}/${date.getMonth() + 1}/${date.getDate()}`
        return `${fullDate} ${hour} ${minutes}`;
      } else {
        return null;
      }
    }
  }
}
